        {
          width,
          height
        }
      );
      this.host.appendChild(this.container);
    }
  };

  this._removeContainerIfEmpty = () => {
    if (this.container && this.container.children.length === 0) {
      this.host.removeChild(this.container);
      this.container = null;
    }
  };

  this.slide = (newTable, direction) => {
    const self = this;
