import {SelectableCollection} from '/coralui-collection';
import '/coralui-component-wait';
import loadIndicator from '../templates/loadIndicator';
import {transform} from '/coralui-util';

const KEYPRESS_TIMEOUT_DURATION = 1000;

/**
