  /** @override */
  _updateFill() {
    const deltaMaxMinBase = 100 / (this.max - this.min);
    const percent = (this._getLowestValue() - this.min) * deltaMaxMinBase;
    const percentDiff = (this._getHighestValue() - this.min) * deltaMaxMinBase - percent;

