  /** @ignore */
  static get observedAttributes() {
    return super.observedAttributes.concat([
      'startvalue',
      'startValue',
      'endvalue',
      'endValue'
    ]);
  }

  /** @ignore */
  connectedCallback() {
    super.connectedCallback();

    // Set filled attribute by default
    this.setAttribute('filled', '');
  }
