   @return {StepListInteractionEnum}
   */
  static get interaction() { return interaction; }

  /** @ignore */
  static get observedAttributes() { return ['target', 'size', 'interaction']; }

  /** @ignore */
  connectedCallback() {
    super.connectedCallback();

    this.classList.add(CLASSNAME);

