class TableCell extends ComponentMixin(HTMLTableCellElement) {
  /**
   The cell's content.

   @type {HTMLElement}
   @contentzone
   */
  get content() {
    return this;
  }
  set content(value) {
    // Support configs
    if (typeof value === 'object') {
      for (const prop in value) {
        /** @ignore */
        this[prop] = value[prop];
      }
    }
  }

  /**
   Whether the table cell is selected.

   @type {Boolean}
   @default false
