    else if (!selectedItems.length) {
      this._selectFirstItem();
    }

    this._setLine();

    this._triggerChangeEvent();
  }

  _setLine(clear) {
    window.requestAnimationFrame(() => {
      const selectedItem = this.selectedItem;

      // Position line under the selected item
