    return this._getContentZone(this._elements.panelStack);
  }
  set panelStack(value) {
    this._setContentZone('panelStack', value, {
      handle: 'panelStack',
      tagName: 'coral-panelstack',
      insert: function(panels) {
        this.appendChild(panels);
      }
    });
