const colorMap = {
  lightblue: 'blue',
  periwinkle: 'blue',
  cyan: 'blue',
  plum: 'red',
  fuchsia: 'red',
  magenta: 'red',
  tangerine: 'orange',
  yellow: 'orange',
  chartreuse: 'green',
  kelly_green: 'green',
  sea_foam: 'green'
