    const all = this.items.getAll();
    return all.length ? all[0].value : '';
  }
  set value(value) {
    this.items.clear();

    if (value) {
      const item = new Tag().set({
        label: {
          innerHTML: value
        },
        value: value
      });

      this._attachInputToItem(item);

      this.items.add(item);
    }
