  16: true,
  17: true,
  18: true,
  91: true
};

/**
 A list of modifier event property names in sorted key code order. Used to add keycodes for modifiers.
 @ignore
 */
const modifierEventPropertyNames = [
  'shiftKey',
  'ctrlKey',
  'altKey',
  'metaKey'
];

/**
 A map of key codes to normalize. These are duplicate keys such as the number pad.
 @ignore
 */
const normalizedCodes = {
  // Numpad 0-9
  96: 48,
  97: 49,
  98: 50,
  99: 51,
  100: 52,
  101: 53,
  102: 54,
  103: 55,
  104: 56,
  105: 57
};

const specialKeyCodes = {
  'backspace': 8,
  'tab': 9,
  'return': 13,
  'enter': 13,
  'pause': 19,
  'capslock': 20,
  'esc': 27,
  'escape': 27,
  'space': 32,
  'pageup': 33,
  'pagedown': 34,
  'end': 35,
  'home': 36,
  'left': 37,
  'up': 38,
  'right': 39,
  'down': 40,
  'insert': 45,
  'del': 46,
  'delete': 46,
  ';': 186,
  '=': 187,
  '*': 106,
  'plus': 107,
  'minus': 189,
  '.': 190,
  'period': 190,
  '/': 191,
  'f1': 112,
  'f2': 113,
  'f3': 114,
  'f4': 115,
  'f5': 116,
  'f6': 117,
  'f7': 118,
  'f8': 119,
  'f9': 120,
  'f10': 121,
  'f11': 122,
  'f12': 123,
  'f13': 124,
  'f14': 125,
  'f15': 126,
  'f16': 127,
  'f17': 128,
  'f18': 129,
  'f19': 130,
  'numlock': 144,
  'scroll': 145,
  ',': 188,
  '`': 192,
  '[': 219,
  '\\': 220,
  ']': 221,
  '\'': 222
