  get returnFocus() {
    return this._returnFocus || returnFocus.OFF;
  }

  set returnFocus(value) {
    value = transform.string(value).toLowerCase();
    this._returnFocus = validate.enumeration(returnFocus)(value) && value || returnFocus.OFF;
