      ) {
        selectableItems.push(child);
      }
    }

    return selectableItems;
  }

  _getAllOffScreen() {
    return Array.prototype.slice.call(this._host.querySelectorAll(`${this._itemTagName}[coral-actionbar-offscreen]`));
  }
}

export default ActionBarContainerCollection;

