  /**
   Returns {@link Datepicker} variants.

   @return {DatepickerVariantEnum}
   */
  static get variant() {
    return variant;
  }

  /**
   Returns {@link Datepicker} types.

   @return {DatepickerTypeEnum}
   */
  static get type() {
    return type;
  }

  static get _attributePropertyMap() {
    return commons.extend(super._attributePropertyMap, {
