  }

  /**
   Returns {@link Popover} variants.

   @return {PopoverVariantEnum}
   */
  static get variant() {
    return variant;
  }

  /**
   Returns {@link Popover} close options.

   @return {PopoverClosableEnum}
   */
  static get closable() {
    return closable;
  }

  /** @ignore */
  static get observedAttributes() {
    return super.observedAttributes.concat([
