  /** @private */
  _dragOnKeyDown(event) {
    switch (event.code) {
      case 'Space':
      case 'ArrowDown':
      case 'ArrowUp':
      case 'Enter':
      case 'Escape':
        event.preventDefault();
        break;
    }
  }

  /** @private */
  _dragOnKeyUp(event) {
    switch (event.code) {
      case 'Space':
