        break;
      case 'ArrowDown':
        this._dragEvents.dispatch('coral-dragaction:dragoveronkeyarrowdown', {
          detail: {
            dragElement: this._dragElement
          }
        });
        break;
      case 'ArrowUp':
        this._dragEvents.dispatch('coral-dragaction:dragoveronkeyarrowup', {
          detail: {
            dragElement: this._dragElement
          }
        });
        break;
      case 'Enter':
      case 'Escape':
        this._dragOnKeyEnd();
        break;
    }
