  static get sequenceTime() {
    return 1500;
  }

  /**
   Convert a combination of keys separated by + into the corresponding code string.
   @ignore
   */
  _keyComboToCodeString(keyCombo) {
    // if single "+"
    if (keyCombo === '+') {
      return keyToCode(keyCombo);
    }

    return keyCombo
      // Convert to string so numbers are supported
      .toString()
      .split('+')
      .map(keyToCode)
